import { useState } from "react";
import "./App.css";
import Card from "./componentes/Card";

function App() {
  const [userInput, setUserInput] = useState([]);
  const [card, setCard] = useState([]);
  const [errorMsg, setErrorMsg] = useState("");

  const handleUserInput = () => {
    fetch(`https://api.github.com/repos/${userInput}`)
      .then((response) => response.json())
      .then((response) => setCard([...card, response]));
    setErrorMsg("");
    setUserInput("");
    if (userInput === "") {
      setErrorMsg("Erro na busca do repositório");
    }
  };

  console.log(card);
  return (
    <div className="App">
      <header className="App-header">
        <h1>GitHub Card</h1>
        <div className="container-input">
          <input
            type="text"
            value={userInput}
            onChange={(event) => setUserInput(event.target.value)}
          />
          <button onClick={() => handleUserInput()}>Search</button>
        </div>

        <span className="error-msg">{errorMsg}</span>
        <div className="container-card">
          {card[card.length].message !== "Not Found" ? (
            card
              .filter((ele) => ele.message !== "Not Found")
              .map((ele, index) => (
                <div key={index}>
                  {" "}
                  <Card card={ele} />{" "}
                </div>
              ))
          ) : (
            <span className="error-msg">"Erro na busca do repositório"</span>
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
