import { useState } from "react";
import "./App.css";
import Card from "./componentes/Card";

function App() {
  const [userInput, setUserInput] = useState([]);
  const [card, setCard] = useState([]);
  const [errorMsg, setErrorMsg] = useState("");

  const handleUserInput = () => {
    fetch(`https://api.github.com/repos/${userInput}`)
      .then((response) => response.json())
      .then((response) => setCard([...card, response]));
    setErrorMsg("");
    setUserInput("");
    if (userInput === "") {
      setErrorMsg("Erro na busca do repositório");
    }
  };

  const { message } = card;

  console.log(message);
  console.log(card);
  return (
    <div className="App">
      <header className="App-header">
        <h1>GitHub Card</h1>
        <div className="container-input">
          <input
            type="text"
            value={userInput}
            onChange={(event) => setUserInput(event.target.value)}
          />
          <button onClick={() => handleUserInput()}>Search</button>
        </div>
        {card[0].message === "Not Found" ? (
          <span className="error-msg">{errorMsg}</span>
        ) : (
          <h>false</h>
        )}
        <span className="error-msg">{errorMsg}</span>
        <div className="container-card">
          {card
            .filter((ele) => ele.message !== "Not Found")
            .map((ele, index) => (
              <div key={index}>
                {" "}
                <Card card={ele} />{" "}
              </div>
            ))}
        </div>
      </header>
    </div>
  );
}

export default App;
