import { useState } from "react";
import "./App.css";
import Card from "./componentes/Card";

function App() {
  const [adress, setAdress] = useState("facebook/react-native");
  const [userInput, setUserInput] = useState("facebook/react-native");
  const [card, setCard] = useState([]);
  const [errorMsg, setErrorMsg] = useState("");

  fetch(`https://api.github.com/repos/${userInput}`)
    .then((response) => response.json())
    .then((response) => setCard([...card, response]));

  console.log(card);

  const handleUserInput = (userInput) => {
    if (userInput === "") {
      setErrorMsg("Erro na busca do repositório");
    } else if (userInput !== "") {
      setAdress(userInput);
      setUserInput("");
      setErrorMsg("");
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>GitHub Card</h1>
        <div className="container-input">
          <input
            type="text"
            value={userInput}
            onChange={(event) => setUserInput([event.target.value])}
          />
          <button
            type="submit"
            onClick={() => {
              handleUserInput(userInput);
            }}
          >
            Search
          </button>
        </div>
        <span className="error-msg">{errorMsg}</span>
        <div className="container-card">
          {card
            .filter((ele) => ele.message !== "Not Found")
            .map((ele) => (
              <div key={ele.id}>
                {" "}
                <Card card={ele} />{" "}
              </div>
            ))}
        </div>
      </header>
    </div>
  );
}

export default App;
